contracts/deps/Aave.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

interface IWETHGateway {
    function depositETH(
        address pool,
        address onBehalfOf,
        uint16 referralCode
    ) external payable;

    function withdrawETH(
        address pool,
        uint256 amount,
        address onBehalfOf
    ) external;
}

/**
 * @title IPoolAddressesProvider
 * @author Aave
 * @notice Defines the basic interface for a Pool Addresses Provider.
 * https://github.com/aave/aave-v3-core/blob/master/contracts/interfaces/IPoolAddressesProvider.sol
 **/
interface IAavePoolAddressesProvider {
    /**
     * @notice Returns the address of the Pool proxy.
     * @return The Pool proxy address
     **/
    function getPool() external view returns (address);
}

// https://github.com/aave/aave-v3-core/blob/master/contracts/interfaces/IPool.sol
interface IAavePool {
    /**
     * @notice Supplies an `amount` of underlying asset into the reserve, receiving in return overlying aTokens.
     * - E.g. User supplies 100 USDC and gets in return 100 aUSDC
     * @param asset The address of the underlying asset to supply
     * @param amount The amount to be supplied
     * @param onBehalfOf The address that will receive the aTokens, same as msg.sender if the user
     *   wants to receive them on his own wallet, or a different address if the beneficiary of aTokens
     *   is a different wallet
     * @param referralCode Code used to register the integrator originating the operation, for potential rewards.
     *   0 if the action is executed directly by the user, without any middle-man
     **/
    function supply(
        address asset,
        uint256 amount,
        address onBehalfOf,
        uint16 referralCode
    ) external;

    /**
     * @notice Withdraws an `amount` of underlying asset from the reserve, burning the equivalent aTokens owned
     * E.g. User has 100 aUSDC, calls withdraw() and receives 100 USDC, burning the 100 aUSDC
     * @param asset The address of the underlying asset to withdraw
     * @param amount The underlying amount to be withdrawn
     *   - Send the value type(uint256).max in order to withdraw the whole aToken balance
     * @param to The address that will receive the underlying, same as msg.sender if the user
     *   wants to receive it on his own wallet, or a different address if the beneficiary is a
     *   different wallet
     * @return The final amount withdrawn
     **/
    function withdraw(
        address asset,
        uint256 amount,
        address to
    ) external returns (uint256);

    /**
     * @notice Returns the state and configuration of the reserve
     * @param asset The address of the underlying asset of the reserve
     * @return The state and configuration data of the reserve
     **/
    function getReserveData(address asset) external view returns (AaveDataTypes.ReserveData memory);
}

// https://github.com/aave/aave-v3-core/blob/master/contracts/protocol/libraries/types/DataTypes.sol
library AaveDataTypes {
    struct ReserveData {
        //stores the reserve configuration
        ReserveConfigurationMap configuration;
        //the liquidity index. Expressed in ray
        uint128 liquidityIndex;
        //the current supply rate. Expressed in ray
        uint128 currentLiquidityRate;
        //variable borrow index. Expressed in ray
        uint128 variableBorrowIndex;
        //the current variable borrow rate. Expressed in ray
        uint128 currentVariableBorrowRate;
        //the current stable borrow rate. Expressed in ray
        uint128 currentStableBorrowRate;
        //timestamp of last update
        uint40 lastUpdateTimestamp;
        //the id of the reserve. Represents the position in the list of the active reserves
        uint16 id;
        //aToken address
        address aTokenAddress;
        //stableDebtToken address
        address stableDebtTokenAddress;
        //variableDebtToken address
        address variableDebtTokenAddress;
        //address of the interest rate strategy
        address interestRateStrategyAddress;
        //the current treasury balance, scaled
        uint128 accruedToTreasury;
        //the outstanding unbacked aTokens minted through the bridging feature
        uint128 unbacked;
        //the outstanding debt borrowed against this asset in isolation mode
        uint128 isolationModeTotalDebt;
    }

    struct ReserveConfigurationMap {
        //bit 0-15: LTV
        //bit 16-31: Liq. threshold
        //bit 32-47: Liq. bonus
        //bit 48-55: Decimals
        //bit 56: reserve is active
        //bit 57: reserve is frozen
        //bit 58: borrowing is enabled
        //bit 59: stable rate borrowing enabled
        //bit 60: asset is paused
        //bit 61: borrowing in isolation mode is enabled
        //bit 62-63: reserved
        //bit 64-79: reserve factor
        //bit 80-115 borrow cap in whole tokens, borrowCap == 0 => no cap
        //bit 116-151 supply cap in whole tokens, supplyCap == 0 => no cap
        //bit 152-167 liquidation protocol fee
        //bit 168-175 eMode category
        //bit 176-211 unbacked mint cap in whole tokens, unbackedMintCap == 0 => minting disabled
        //bit 212-251 debt ceiling for isolation mode with (ReserveConfiguration::DEBT_CEILING_DECIMALS) decimals
        //bit 252-255 unused

        uint256 data;
    }
}

contracts/interfaces/ITokenPool.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

/**
 * @title Yieldgate Token Pool Interface
 * @author Sebastian Stammler <seb@yieldgate.xyz>
 */
interface ITokenPool {
    /**
     * @notice Staked is emitted on every successful stake.
     * @param token Address of staked ERC20 token.
     * @param supporter Addrses of staker.
     * @param amount Amount that got staked.
     */
    event Staked(address indexed token, address indexed supporter, uint256 amount);

    /**
     * @notice Unstaked is cool
     * @param token Address of unstaked ERC20 token.
     * @param supporter Addrses of unstaker.
     * @param amount Amount that got unstaked.
     */
    event Unstaked(address indexed token, address indexed supporter, uint256 amount);

    /**
     * @notice Claimed is emitted on every successful claim.
     * @param token Address of claimed ERC20 token.
     * @param amount Amount that got claimed.
     */
    event Claimed(address indexed token, uint256 amount);

    /**
     * @notice Returns the beneficiary of this pool.
     * @dev Usually this is implemented as a public (immutable) address storage
     * variable.
     */
    function beneficiary() external view returns (address);

    /**
     * @notice Stakes given amount of token on behalf of the provided supporter.
     * @dev Prio to calling stake, a respective allowance for the token pool has
     *   to be set. On success, the implementation must emit a Staked event.
     * @param token Address of ERC20 token to stake.
     * @param supporter The supporter on whose behalf the token is staked.
     * @param amount The amount of token to stake.
     */
    function stake(
        address token,
        address supporter,
        uint256 amount
    ) external;

    /**
     * @notice Unstakes all previously staked token by the calling supporter.
     *   The beneficiary keeps all generated yield.
     * @dev On success, the implementation must emit an Unstaked event.
     * @param token Address of ERC20 token to unstake.
     * @return Returns the unstaked amount.
     */
    function unstake(address token) external returns (uint256);

    /**
     * @notice Sends the accrued yield to the beneficiary of this pool.
     * @dev The implementation should enforce some access control to this
     *   function, e.g., only let it be callable by the beneficiary. It must
     *   emit a Claimed event on success.
     * @param token Address of ERC20 token to claim.
     * @return Returns the claimed amount of yield.
     */
    function claim(address token) external returns (uint256);

    /**
     * @notice Queries the claimable yield for the given ERC20 token.
     * @param token Address of ERC20 token to query.
     * @return Returns the claimable yield.
     */
    function claimable(address token) external view returns (uint256);

    /**
     * @notice Queries the total staked amount for the given ERC20 token.
     * @param token Address of ERC20 token to query.
     * @return Returns the total staked amount.
     */
    function staked(address token) external view returns (uint256);
}

contracts/test/AaveMock.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import {IAavePoolAddressesProvider, IAavePool, AaveDataTypes} from "../deps/Aave.sol";

/**
 * @title AavePool and aToken mock for testnets
 * @author Sebastian Stammler
 * @notice AaveMock mocks the Aave Pool, PoolAddressesProvider and also is the
 * aToken for a single external ERC20 reference token.
 * @dev Reference token deposited into this contract with function `supply`
 * yields an emulated linear return until a specified doubling time, from which
 * point on it stays the same until another action changes the token balance
 * again, either in- or decreasing it.
 * The AaveMock is also the aToken of the reference token.
 */
contract AaveMock is IAavePool, IAavePoolAddressesProvider, ERC20, Ownable {
    using SafeERC20 for IERC20Metadata;
    using Math for uint256;

    IERC20Metadata public immutable rToken;
    uint256 public doublingDuration;

    mapping(address => uint256) private lastBalanceUpdate; // account -> timestamp

    modifier onlyRToken(address token) {
        require(token == address(rToken), "only rToken allowed");
        _;
    }

    constructor(address _rToken, uint256 _doublingDuration)
        ERC20("Stake for Earth Testnet aToken", "aSFE")
    {
        rToken = IERC20Metadata(_rToken);
        doublingDuration = _doublingDuration;
    }

    function decimals() public view override returns (uint8) {
        return rToken.decimals();
    }

    function getPool() external view returns (address) {
        return address(this);
    }

    function setDoublingDuration(uint256 _doublingDuration) external onlyOwner {
        doublingDuration = _doublingDuration;
    }

    function supply(
        address asset,
        uint256 amount,
        address onBehalfOf,
        uint16 /* referralCode */
    ) external onlyRToken(asset) {
        rToken.safeTransferFrom(msg.sender, address(this), amount);
        updateBalance(onBehalfOf);
        _mint(onBehalfOf, amount);
    }

    function withdraw(
        address asset,
        uint256 amount,
        address to
    ) external onlyRToken(asset) returns (uint256) {
        address account = msg.sender;
        if (amount == type(uint256).max) {
            amount = balanceOf(account);
        }
        updateBalance(account);
        _burn(account, amount);
        // TODO: only works if target token is minted after balance update
        rToken.safeTransfer(to, amount);
        return amount;
    }

    function updateBalance(address account) internal {
        uint256 balance = ERC20.balanceOf(account);
        lastBalanceUpdate[account] = block.timestamp;
        if (balance == 0) return; // initial update

        uint256 newBalance = balanceOf(account);
        _mint(account, newBalance - balance);
    }

    // Let balanceOf peek into the future, so that claimable() works
    function balanceOf(address account) public view virtual override returns (uint256) {
        uint256 lastUpdate = lastBalanceUpdate[account];
        uint256 balance = ERC20.balanceOf(account);
        uint256 diff = block.timestamp - lastUpdate;
        if (lastUpdate == 0 || balance == 0 || diff == 0) return balance;

        if (diff > doublingDuration) {
            diff = doublingDuration;
        }

        return balance.mulDiv(doublingDuration + diff, doublingDuration);
    }

    function getReserveData(address asset)
        external
        view
        onlyRToken(asset)
        returns (AaveDataTypes.ReserveData memory data)
    {
        data.aTokenAddress = address(this);
        return data;
    }
}

contracts/test/ERC20AutoMinter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/**
 * @title ERC20AutoMinter
 * @author Sebastian Stammler
 * @notice This token automatically mints new token when approvals are set or
 * transfers are made, up to a specified maximum.
 * Anyone can also mint token to themselves using function mintMe or to anyone
 * else using function mint.
 * @dev Only the contract owner can reset the max. mint amount.
 */
abstract contract ERC20AutoMinter is ERC20, Ownable {
    uint256 public maxMint;

    constructor(uint256 _maxMint) {
        maxMint = _maxMint;
    }

    function setMaxMint(uint256 _maxMint) external onlyOwner {
        maxMint = _maxMint;
    }

    // approve auto-mints up to maxMint token to the caller
    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        mintMe(amount);
        return ERC20.approve(spender, amount);
    }

    // transfer auto-mints up to maxMint token to the caller
    function transfer(address to, uint256 amount) public virtual override returns (bool) {
        mintMe(amount);
        return ERC20.transfer(to, amount);
    }

    // anyone can mint themselves up to maxMint token
    function mintMe(uint256 amount) public returns (uint256) {
        return mint(_msgSender(), amount);
    }

    // anyone can mint anyone else up to maxMint token
    function mint(address account, uint256 amount) public returns (uint256) {
        uint256 mintAmount = maxAmount(amount);
        _mint(account, mintAmount);
        return mintAmount;
    }

    function maxAmount(uint256 amount) internal view returns (uint256) {
        return amount > maxMint ? maxMint : amount;
    }
}

contract SFETestUSD is ERC20AutoMinter {
    constructor(uint256 maxMint)
        ERC20AutoMinter(maxMint)
        ERC20("Stake for Earth Testnet USD", "SFE")
    {} // solhint-disable-line no-empty-blocks

    function decimals() public view virtual override returns (uint8) {
        return 6;
    }
}

contracts/test/OffsetHelperMock.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import {IToucanOffsetHelper} from "../deps/Toucan.sol";

/**
 * @title Toucan OffsetHelper mock
 * @author Sebastian Stammler
 * @notice Mocks offsetting by any token by transferring the token to this
 * contract and then emitting a mock Redeemed event.
 * @dev The contract owner can set rates for individual tokens with setRate. If
 * no rate is set, a default rate of 1 will be used.
 * The Redeemed event will have two TCO2 entries, which the TCO2 addresses being
 * the provided pool token address +1 and +2.
 */
contract OffsetHelperMock is IToucanOffsetHelper, Ownable {
    using SafeERC20 for IERC20Metadata;
    using Math for uint256;

    struct Rate {
        uint256 mult;
        uint256 denom;
    }

    mapping(address => Rate) internal rates;

    function setRate(
        address token,
        uint256 rateMult,
        uint256 rateDenom
    ) external onlyOwner {
        rates[token] = Rate({mult: rateMult, denom: rateDenom});
    }

    function autoOffsetExactInToken(
        address _fromToken,
        uint256 _amountToSwap,
        address _poolToken
    ) external returns (address[] memory tco2s, uint256[] memory amounts) {
        IERC20Metadata(_fromToken).safeTransferFrom(msg.sender, address(this), _amountToSwap);
        uint256 amount = calculateExpectedPoolTokenForToken(_fromToken, _amountToSwap, _poolToken);
        (tco2s, amounts) = mockTCO2s(_poolToken, amount);
        emit Redeemed(msg.sender, _poolToken, tco2s, amounts);
    }

    function calculateExpectedPoolTokenForToken(
        address _fromToken,
        uint256 _fromAmount,
        address /* _toToken ignored */
    ) public view returns (uint256) {
        Rate memory rate = rates[_fromToken];
        if (rate.mult == 0) {
            // if no rate set, default rate is 1/1
            return _fromAmount;
        }
        return _fromAmount.mulDiv(rate.mult, rate.denom);
    }

    function mockTCO2s(address tokenBase, uint256 amount)
        internal
        pure
        returns (address[] memory tco2s, uint256[] memory amounts)
    {
        (tco2s, amounts) = (new address[](2), new uint256[](2));
        uint160 base = uint160(tokenBase);
        tco2s[0] = address(base + 1);
        tco2s[1] = address(base + 2);
        uint256 half = amount / 2;
        amounts[0] = half;
        amounts[1] = amount - half;
    }
}

contracts/TokenPool.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {ITokenPool} from "./interfaces/ITokenPool.sol";
import {IAavePool, IAavePoolAddressesProvider} from "./deps/Aave.sol";

/**
 * @title Yieldgate Token Pool
 * @author Sebastian Stammler
 * @notice Users can stake any token on the TokenPool while a designated
 * beneficiary can claim any generated yield. Users can unstake their previously
 * staked tokens at any time. Aave is used as a yield generator.
 * @dev Prior to staking a new token, the AavePool has to be approved as a
 * spender of this contract's token once by calling approvePool with the token
 * address. The alternative constructor contract TokenPoolWithApproval can be
 * used to deploy this contract and approve a list of tokens at the same time.
 */
contract TokenPool is ITokenPool {
    /*
     * @notice Provider of AAVE protocol contract instance addresses. This
     *   address is fixed for a particular market.
     * @dev Since the actual AAVE Pool address is subject to change, AAVE
     *   advices to always read the pool address from the PoolAddressesProvider.
     */
    IAavePoolAddressesProvider public immutable aavePoolAddressesProvider;

    /*
     * @notice address of beneficiary that can claim generated yield.
     */
    address public immutable beneficiary;

    /*
     * @notice Amount staked by token and by user.
     * @dev Mapping format is token address -> user -> amount.
     */
    mapping(address => mapping(address => uint256)) public stakes;

    // Total stake, by token address.
    mapping(address => uint256) internal totalStake;

    modifier onlyBeneficiary() {
        require(msg.sender == beneficiary, "only beneficiary");
        _;
    }

    constructor(address _aavePoolAddressesProvider, address _beneficiary) {
        aavePoolAddressesProvider = IAavePoolAddressesProvider(_aavePoolAddressesProvider);
        beneficiary = _beneficiary;
    }

    /*
     * @notice Approves the Aave Pool to spend the given token on behalf of this
     * token pool. Trusting the Aave pool implementation, the maximum allowance
     * is set to save on repeated approve calls.
     * @dev Has to be called once before staking a new token, by any user.
     * A new call would be necessary in the unlikely event that the Aave pool
     * proxy address, returned by the PoolAddressesProvider, changes.
     */
    function approvePool(address token) public {
        require(
            IERC20(token).approve(address(aavePool()), type(uint256).max),
            "AavePool approval failed"
        );
    }

    /**
     * @inheritdoc ITokenPool
     * @dev Prio to calling stake, a respective allowance for the token pool has
     * to be set.
     * When staking a token for the first time, the (infinite) ERC20 allowance
     * for the Aave Pool has to be approved first by calling function
     * approvePool (with any user). stake emits a Staked event on success.
     */
    function stake(
        address token,
        address supporter,
        uint256 amount
    ) public virtual {
        require(amount > 0, "zero amount");

        stakes[token][supporter] += amount;
        totalStake[token] += amount;

        require(
            IERC20(token).transferFrom(msg.sender, address(this), amount),
            "token transfer failed"
        );
        // For the next step to succeed, approvePool must have been called once before.
        aavePool().supply(token, amount, address(this), 0);

        emit Staked(token, supporter, amount);
    }

    /// @inheritdoc ITokenPool
    function unstake(address token) public virtual returns (uint256) {
        address supporter = msg.sender;
        uint256 amount = stakes[token][supporter];
        require(amount > 0, "no supporter");

        stakes[token][supporter] = 0;
        totalStake[token] -= amount;

        withdraw(token, amount, supporter);

        emit Unstaked(token, supporter, amount);
        return amount;
    }

    /**
     * @inheritdoc ITokenPool
     * @dev Emits a Claimed event on success. Only callable by the beneficiary.
     */
    function claim(address token) public virtual onlyBeneficiary returns (uint256) {
        uint256 amount = claimable(token);
        withdraw(token, amount, beneficiary);

        emit Claimed(token, amount);
        return amount;
    }

    function withdraw(
        address token,
        uint256 amount,
        address receiver
    ) internal {
        aavePool().withdraw(token, amount, receiver);
    }

    /// @inheritdoc ITokenPool
    function claimable(address token) public view returns (uint256) {
        IERC20 aToken = IERC20(aavePool().getReserveData(token).aTokenAddress);
        return aToken.balanceOf(address(this)) - staked(token);
    }

    /// @inheritdoc ITokenPool
    function staked(address token) public view returns (uint256) {
        return totalStake[token];
    }

    function aavePool() internal view returns (IAavePool) {
        return IAavePool(aavePoolAddressesProvider.getPool());
    }
}

/**
 * @dev The TokenPoolWithApproval is the same contract as the
 * TokenPool while its constructor also approves the aavePool to spend the
 * provided list of tokens on behalf of this TokenPool.
 */
contract TokenPoolWithApproval is TokenPool {
    constructor(
        address _aavePoolAddressesProvider,
        address _beneficiary,
        address[] memory _approvedTokens
    ) TokenPool(_aavePoolAddressesProvider, _beneficiary) {
        for (uint256 i = 0; i < _approvedTokens.length; i++) approvePool(_approvedTokens[i]);
    }
}

contracts/ToucanOffsetter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./deps/Toucan.sol";

import "./TokenPool.sol";
import "./interfaces/ITokenPool.sol";

/**
 * @title Yieldgate Toucan Offsetter
 * @author Sebastian Stammler
 * @notice The Toucan Offsetter contract is designed to be the beneficiary of a
 * Yieldgate TokenPool, forwarding all generated yield to Toucan for carbon
 * offsetting.
 * @dev The beneficiary is set at deployment of a TokenPool. For that reason,
 * the ToucanOffsetter has to be deployed first, then the TokenPool with the
 * just deployed ToucanOffsetter as beneficiary and then finally the TokenPool
 * has to be set using setYGPool.
 * The alternative constructors of ToucanOffsetterPoolDeployment or
 * ToucanOffsetterPoolDeploymentApproval can be used to automate this process
 * within a single deployment transaction.
 */
contract ToucanOffsetter is AccessControl {
    using SafeERC20 for IERC20;

    /**
     * @notice Offsetters are allowed to call offsetYield. Only this contract's
     *   admin can grant this role to new addresses.
     */
    bytes32 public constant OFFSET_ROLE = keccak256("OFFSET_ROLE");

    /// @notice TokenPool from which yield is claimed.
    ITokenPool public pool;

    /// @notice Toucan OffsetHelper contract that is used for offsetting claimed yield.
    IToucanOffsetHelper public offsetHelper;

    /**
     * @notice YGPoolSet is emitted when the yieldgate pool is (re)set.
     * @param pool Address of Yieldgate pool.
     */
    event YGPoolSet(address indexed pool);

    /**
     * @notice Offset is emitted when yield has been claimed from the
     * TokenPool and used for offsetting via Toucan offset tokens.
     * @dev The last parameter offset only contains the sum of all offset TCO2s.
     * If individual TCO2s and amounts are of interest to the application, read
     * the Redeemed event emitted by the Toucan OffsetHelper.
     * @param token Address of input token that got swapped into Toucan offset token.
     * @param yield Amount of yield that got swapped.
     * @param offsetToken Toucan offset token that got redeemed.
     * @param offset Total sum of TCO2s that got offset.
     */
    event Offset(address indexed token, uint256 yield, address indexed offsetToken, uint256 offset);

    modifier onlyAdmin() {
        _checkRole(DEFAULT_ADMIN_ROLE);
        _;
    }

    /**
     * @notice Deploys the ToucanOffsetter with given Toucan OffsetHelper.
     * @dev TokenPool has to be deployed afterwards, setting this
     * ToucanOffsetter as beneficiary. Then setYGPool has to be called with the
     * TokenPool's address. ToucanOffsetterPoolDeployment can be used instead to
     * bundle this behavior.
     * The deployer is set as admin and also granted the offsetter role.
     * @param _toucanOffsettHelper Address of Toucan OffsetHelper.
     */
    constructor(address _toucanOffsettHelper) {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(OFFSET_ROLE, msg.sender);

        offsetHelper = IToucanOffsetHelper(_toucanOffsettHelper);
    }

    /**
     * @notice Sets the Yieldgate TokenPool.
     * @dev Can only be set by the admin.
     * @param _pool TokenPool address.
     */
    function setYGPool(address _pool) external onlyAdmin {
        _setYGPool(_pool);
    }

    function _setYGPool(address _pool) internal {
        pool = ITokenPool(_pool);
        emit YGPoolSet(_pool);
    }

    /**
     * @notice offsetYield claims all accrued yield from the TokenPool and then
     * uses the OffsetHelper to swap and retire the yield into Toucan offset
     * token.
     * @dev Emits an Offset event. Caller must have OFFSET_ROLE.
     * @param token Token to claim yield for from the TokenPool.
     * @param offsetToken Toucan offset token to use for offsetting (BCT, NCT, ...).
     */
    function offsetYield(address token, address offsetToken)
        external
        virtual
        onlyRole(OFFSET_ROLE)
    {
        uint256 yield = pool.claim(token);
        IERC20(token).safeApprove(address(offsetHelper), yield);
        (, uint256[] memory amounts) = offsetHelper.autoOffsetExactInToken(
            token,
            yield,
            offsetToken
        );

        uint256 offset;
        for (uint256 i = 0; i < amounts.length; i++) offset += amounts[i];
        emit Offset(token, yield, offsetToken, offset);
    }
}

/**
 * @dev The ToucanOffsetterPoolDeployment is the same contract as the
 * ToucanOffsetter while its constructor also deploys its own TokenPool, setting
 * it as its pool during deployment.
 */
contract ToucanOffsetterWithPoolDeployment is ToucanOffsetter {
    constructor(address _toucanOffsettHelper, address _aavePoolAddressesProvider)
        ToucanOffsetter(_toucanOffsettHelper)
    {
        // deploy own pool
        pool = new TokenPool(_aavePoolAddressesProvider, address(this));
        _setYGPool(address(pool));
    }
}

/**
 * @dev The ToucanOffsetterPoolDeploymentApproval is the same contract as the
 * ToucanOffsetter while its constructor also deploys its own TokenPool, setting
 * it as its pool during deployment and approves the Aave pool to transfer the
 * provided list of tokens on behalf of the TokenPool.
 */
contract ToucanOffsetterWithPoolDeploymentApproval is ToucanOffsetter {
    constructor(
        address _toucanOffsettHelper,
        address _aavePoolAddressesProvider,
        address[] memory _approvedTokens
    ) ToucanOffsetter(_toucanOffsettHelper) {
        // deploy own pool
        pool = new TokenPoolWithApproval(
            _aavePoolAddressesProvider,
            address(this),
            _approvedTokens
        );
        _setYGPool(address(pool));
    }
}

contracts/YieldGate.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import {IWETHGateway} from "./deps/Aave.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";

contract YieldGate {
    event PoolDeployed(address indexed beneficiary, address indexed deployer, address pool);

    address private immutable beneficiaryPoolLib;
    address public immutable aavePool;
    IWETHGateway public immutable wethgw;
    IERC20 public immutable token;

    // beneficiary => BeneficiaryPool
    mapping(address => BeneficiaryPool) public beneficiaryPools;

    constructor(
        address _pool,
        address wethGateway,
        address aWETH
    ) {
        aavePool = _pool;
        wethgw = IWETHGateway(wethGateway);
        token = IERC20(aWETH);

        BeneficiaryPool bp = new BeneficiaryPool();
        // init it so no one else can (RIP Parity Multisig)
        bp.init(address(this), msg.sender);
        beneficiaryPoolLib = address(bp);
    }

    function deployPool(address beneficiary) external returns (address) {
        BeneficiaryPool bpool = BeneficiaryPool(Clones.clone(beneficiaryPoolLib));
        bpool.init(address(this), beneficiary);
        beneficiaryPools[beneficiary] = bpool;

        emit PoolDeployed(beneficiary, msg.sender, address(bpool));
        return address(bpool);
    }

    // claimable returns the total earned ether by the provided beneficiary.
    // It is the accrued interest on all staked ether.
    // It can be withdrawn by the beneficiary with claim.
    function claimable(address beneficiary) public view returns (uint256) {
        BeneficiaryPool bpool = beneficiaryPools[beneficiary];
        if (address(bpool) == address(0)) {
            return 0;
        }
        return bpool.claimable();
    }

    // staked returns the total staked ether on behalf of the beneficiary.
    function staked(address beneficiary) public view returns (uint256) {
        BeneficiaryPool bpool = beneficiaryPools[beneficiary];
        if (address(bpool) == address(0)) {
            return 0;
        }
        return bpool.staked();
    }

    // returns the total staked ether by the supporter and the timeout until
    // which the stake is locked.
    function supporterStaked(address supporter, address beneficiary)
        public
        view
        returns (uint256, uint256)
    {
        BeneficiaryPool bpool = beneficiaryPools[beneficiary];
        if (address(bpool) == address(0)) {
            return (0, 0);
        }
        return (bpool.stakes(supporter), bpool.lockTimeout(supporter));
    }
}

contract BeneficiaryPool {
    event Staked(
        address indexed beneficiary,
        address indexed supporter,
        uint256 amount,
        uint256 lockTimeout
    );
    event Unstaked(address indexed beneficiary, address indexed supporter, uint256 amount);
    event Claimed(address indexed beneficiary, uint256 amount);
    event ParametersChanged(address indexed beneficiary, uint256 minAmount, uint256 minDuration);

    YieldGate public gate;
    address public beneficiary;

    // Minimum required amount to stake.
    uint256 public minAmount;
    // Minimum required staking duration (in seconds).
    uint256 public minDuration;
    // Records when a supporter is allowed to unstake again. This has the added
    // benefit that future changes to the duration do not affect current stakes.
    mapping(address => uint256) internal lockTimeouts;

    // supporter => amount
    mapping(address => uint256) public stakes;
    // total staked amount
    uint256 internal totalStake;

    modifier onlyBeneficiary() {
        require(msg.sender == beneficiary, "only beneficiary");
        _;
    }

    // Initializes this contract's parameters after deployment. This is called
    // by the pool factory, i.e. the Yieldgate main contract, right after
    // deployment. Can only be called once.
    function init(address _gate, address _beneficiary) public {
        require(address(gate) == address(0), "already initialized");

        gate = YieldGate(_gate);
        beneficiary = _beneficiary;

        emitParametersChanged(0, 0);
    }

    // To save gas, add individual parameter setters.

    function setMinAmount(uint256 _minAmount) external onlyBeneficiary {
        minAmount = _minAmount;
        emitParametersChanged(_minAmount, minDuration);
    }

    function setMinDuration(uint256 _minDuration) external onlyBeneficiary {
        minDuration = _minDuration;
        emitParametersChanged(minAmount, _minDuration);
    }

    function setParameters(uint256 _minAmount, uint256 _minDuration) external onlyBeneficiary {
        minAmount = _minAmount;
        minDuration = _minDuration;
        emitParametersChanged(_minAmount, _minDuration);
    }

    function emitParametersChanged(uint256 _minAmount, uint256 _minDuration) internal {
        emit ParametersChanged(beneficiary, _minAmount, _minDuration);
    }

    // Stakes the sent ether on behalf of the provided supporter. The supporter
    // is usually msg.sender if staking on the transaction sender's behalf.
    // The staking timeout is reset on each call, so prior stake is re-locked.
    function stake(address supporter) public payable {
        uint256 amount = msg.value;
        require(amount > 0 && stakes[supporter] + amount >= minAmount, "amount too low");

        stakes[supporter] += amount;
        totalStake += amount;
        uint256 timeout = 0;
        if (minDuration > 0) {
            timeout = block.timestamp + minDuration;
        }
        lockTimeouts[supporter] = timeout;

        gate.wethgw().depositETH{value: amount}(gate.aavePool(), address(this), 0);
        emit Staked(beneficiary, supporter, amount, timeout);
    }

    // Unstakes all previously staked ether by the calling supporter.
    // The beneficiary keeps all generated yield.
    // If a minimum staking duration was set by the beneficiary at the time of
    // staking, it is checked that the timeout has elapsed.
    function unstake() public returns (uint256) {
        address supporter = msg.sender;
        require(block.timestamp >= lockTimeout(supporter), "stake still locked");

        uint256 amount = stakes[supporter];
        require(amount > 0, "no supporter");

        stakes[supporter] = 0;
        totalStake -= amount;

        withdraw(amount, supporter);
        emit Unstaked(beneficiary, supporter, amount);
        return amount;
    }

    // claim sends the accrued interest to the beneficiary of this pool. Staked
    // ether remains at the yield pool and continues generating yield.
    function claim() public onlyBeneficiary returns (uint256) {
        uint256 amount = claimable();
        withdraw(amount, beneficiary);
        emit Claimed(beneficiary, amount);
        return amount;
    }

    function withdraw(uint256 amount, address receiver) internal {
        require(gate.token().approve(address(gate.wethgw()), amount), "ethgw approval failed");
        gate.wethgw().withdrawETH(gate.aavePool(), amount, receiver);
    }

    // claimable returns the total earned ether by the provided beneficiary.
    // It is the accrued interest on all staked ether.
    // It can be withdrawn by the beneficiary with claim.
    function claimable() public view returns (uint256) {
        return gate.token().balanceOf(address(this)) - staked();
    }

    // staked returns the total staked ether by this beneficiary pool.
    function staked() public view returns (uint256) {
        return totalStake;
    }

    // lockTimeout returns the effective timeout until when a supporter's stake
    // is locked. If the minDuration is 0 it is always 0, possibly disregarding
    // an old lock from prior staking when minDuration was > 0.
    function lockTimeout(address supporter) public view returns (uint256) {
        // Set timeout to 0 if minDuration == 0 because a supporter could then
        // trivially reset their lock timeout by staking and then immediately
        // unstaking anyways.
        if (minDuration == 0) {
            return 0;
        }
        return lockTimeouts[supporter];
    }
}
